mapper.delete:
DELETE FROM cb_active_order_detail 


		AND id = #{id}


		AND client_id = #{clientId}


		AND create_time = #{createTime}


		AND update_time = #{updateTime}


		AND order_id = #{orderId}


		AND order_market_type = #{orderMarketType}


		AND active_id = #{activeId}


		AND type = #{type}


		AND sub_order_id = #{subOrderId}


		AND product_id = #{productId}


		AND product_name = #{productName}


		AND product_price = #{productPrice}


		AND product_vip_price = #{productVipPrice}


		AND org_normal_price_market = #{orgNormalPriceMarket}


		AND org_normal_price_count = #{orgNormalPriceCount}


		AND normal_price_market = #{normalPriceMarket}


		AND normal_price_count = #{normalPriceCount}



		AND org_vip_price_market = #{orgVipPriceMarket}


		AND org_vip_price_count = #{orgVipPriceCount}



		AND vip_price_market = #{vipPriceMarket}


		AND vip_price_count = #{vipPriceCount}



		AND ticket_id = #{ticketId}

