mapper.delete:
DELETE FROM cb_vouchers_info 


		AND id = #{id}


		AND client_id = #{clientId}


		AND create_time = #{createTime}


		AND update_time = #{updateTime}


		AND active_id = #{activeId}


		AND vouchers_id = #{vouchersId}


		AND vouchers_money = #{vouchersMoney}


		AND vouchers_use_money = #{vouchersUseMoney}


		AND vouchers_res_money = #{vouchersResMoney}


		AND member_code = #{memberCode}


		AND send_active_id = #{sendActiveId}


		AND send_time = #{sendTime}


		AND status = #{status}


		AND use_time = #{useTime}


		AND user_member_code = #{userMemberCode}

mapper.update:
UPDATE cb_vouchers_info 
set update_time = #{updateTime},
    status = #{status},
    user_member_code = #{userMemberCode},
    use_time = #{useTime}
where vouchers_id = #{vouchersId}

mapper.select:
		SELECT 
			 , a.active_name, a.description, a.expire_end_time, a.expire_start_time, a.type
        FROM cb_vouchers_info v left join cb_active_base_rule a on v.active_id = a.active_id


				 AND v.id = #{id}


				 AND v.client_id = #{clientId}


				 AND v.create_time = #{createTime}


				 AND v.update_time = #{updateTime}


				 AND v.active_id = #{activeId}


				 AND v.vouchers_id = #{vouchersId}


				 AND v.vouchers_money = #{vouchersMoney}


				 AND v.vouchers_use_money = #{vouchersUseMoney}


				 AND v.vouchers_res_money = #{vouchersResMoney}


				 AND v.member_code = #{memberCode}


				 AND v.send_active_id = #{sendActiveId}


				 AND v.send_time = #{sendTime}


				 AND v.status = #{status}


				 AND v.use_time = #{useTime}


				 AND v.user_member_code = #{userMemberCode}

		 LIMIT 1

