SELECT * FROM ( SELECT * FROM cb_vouchers_info v UNION SELECT * FROM cb_vouchers_expired_info e UNION SELECT * FROM cb_vouchers_use_info u) as voucher where 1=1
AND voucher.client_id = #{vouchersInfo.clientId}
AND voucher.active_id = #{vouchersInfo.activeId}

select voucher.vouchers_Id,voucher.vouchers_money,voucher.vouchers_use_money,
o.order_id,o.member_code,o.phone,o.detail,c.`name`,o.order_money,o.pay_time,
o.pay_bill_no,o.return_bill_no,o.return_money from ( 
select * from cb_vouchers_info v where  
v.active_id = #{vouchersInfo.activeId} and v.client_id = #{vouchersInfo.clientId} 
UNION 
select * from cb_vouchers_expired_info e where e.active_id = #{vouchersInfo.activeId} 
and e.client_id = #{vouchersInfo.clientId} 
UNION 
select * from cb_vouchers_use_info u where u.active_id = #{vouchersInfo.activeId} 
and u.client_id = #{vouchersInfo.clientId} 
) as voucher,cb_active_order_detail d,cb_active_order o,cb_client c 
where voucher.vouchers_id = d.ticket_id 
and o.client_id = c.client_id 
AND d.order_id = o.order_id

select voucher.vouchers_Id,voucher.vouchers_money,voucher.vouchers_use_money,
o.order_id,o.member_code,o.phone,o.detail,c.`name`,o.order_money,o.pay_time,d.product_id,
d.normal_price_market,d.vip_price_market,o.pay_bill_no,o.return_bill_no,o.return_money from ( 
select * from cb_vouchers_info v where  
v.active_id = #{vouchersInfo.activeId} and v.client_id = #{vouchersInfo.clientId} 
UNION 
select * from cb_vouchers_expired_info e where e.active_id = #{vouchersInfo.activeId} 
and e.client_id = #{vouchersInfo.clientId} 
UNION 
select * from cb_vouchers_use_info u where u.active_id = #{vouchersInfo.activeId} 
and u.client_id = #{vouchersInfo.clientId} 
) as voucher,cb_active_order_detail d,cb_active_order o,cb_client c 
where voucher.vouchers_id = d.ticket_id 
and o.client_id = c.client_id 
AND d.order_id = o.order_id  
AND o.`status` = '1' AND o.pay_time > 0
GROUP BY o.order_id;


	SELECT * FROM cb_vouchers_temporary_info where 1=1


