mapper.select:
select 
	id,create_time,client_id,uri,name,point_partner_no,openapi_partner_no,order_display,appkey,appsecret,bank_type,bank_name,qr_logo,return_url,remark,bank_id,settle_pay_org,bank_org,bank_ZH     
from cb_client 


			and id = #{id}


			and client_id = #{clientId}


			and uri = #{uri}


			and create_time = #{createTime}


			and name = #{name}


			and point_partner_no = #{pointPartnerNo}


			and openapi_partner_no = #{openapiPartnerNo}


			and order_display = #{orderDisplay}


			and appkey = #{appkey}


			and appsecret = #{appsecret}


			and bank_type = #{bankType}


			and bank_name = #{bankName}


			and qr_logo = #{qrLogo}


			and return_url = #{returnUrl}


			and remark = #{remark}


			and bank_id = #{bankId}   


			and settle_pay_org = #{settlePayOrg}   


			and bank_org = #{bankOrg}   


			and bank_ZH = #{bankZH}

select 
	id,create_time,client_id,uri,name,point_partner_no,openapi_partner_no,order_display,appkey,appsecret,bank_type,bank_name,qr_logo,return_url,remark,bank_id,settle_pay_org,bank_org,bank_ZH    
from cb_client 



			and id = #{client.id}


			and client_id = #{client.clientId}


			and uri = #{client.uri}


			and create_time = #{client.createTime}


			and client_id = #{client.clientId}


			and name = #{client.name}


			and point_partner_no = #{client.pointPartnerNo}


			and openapi_partner_no = #{client.openapiPartnerNo}


			and order_display = #{client.orderDisplay}


			and appkey = #{client.appkey}


			and appsecret = #{client.appsecret}


			and bank_type = #{client.bankType}


			and bank_name = #{client.bankName}


			and qr_logo = #{client.qrLogo}


			and return_url = #{client.returnUrl}


			and remark = #{client.remark}


			and bank_id = #{client.bankId}   


			and settle_pay_org = #{client.settlePayOrg} 


			and bank_org = #{client.bankOrg}   


			and bank_ZH = #{client.bankZH}    



order by create_time desc
