mapper.select:
SELECT id,client_id,activity_name,activity_no,operator,activity_desc,status,activity_type,logo_url,create_time,update_time  
FROM cb_recommend_activity_tag 


		AND client_id = #{rat.clientId} 


		AND status IN (#{rat.status}) 


		AND activity_name LIKE CONCAT('%', #{rat.activityName}, '%') 


		AND activity_type = #{rat.activityType}

SELECT id,client_id,activity_name,activity_no,operator,activity_desc,status,activity_type,logo_url,create_time,update_time 
FROM cb_recommend_activity_tag 


		AND id = #{rat.id} 


		AND client_id = #{rat.clientId} 


		AND activity_no = #{rat.activityNo} 


		AND activity_type = #{rat.activityType}

mapper.update:
UPDATE cb_recommend_activity_tag 


		activity_desc = #{rat.activityDesc}, 


		status = #{rat.status}, 


		operator = #{rat.operator}, 


		logo_url = #{rat.logoUrl}, 


		update_time = #{rat.updateTime}, 




		AND id = #{rat.id} 


		AND client_id = #{rat.clientId} 


		AND activity_no = #{rat.activityNo} 


		AND activity_type = #{rat.activityType}
