mapper.delete:
DELETE FROM cb_process_config 


		AND id = #{id}


		AND create_time = #{createTime}


		AND update_time = #{updateTime}


		AND client_id = #{clientId}


		AND org_code_leve = #{orgCodeLeve}


		AND type = #{type}


		AND type_detail = #{typeDetail}


		AND process_id = #{processId}


		AND creator = #{creator}

mapper.update:
UPDATE cb_process_config 


		id = #{id}, 


		create_time = #{createTime}, 


		update_time = #{updateTime}, 


		client_id = #{clientId}, 


		org_code_leve = #{orgCodeLeve}, 


		type = #{type}, 


		type_detail = #{typeDetail}, 


		process_id = #{processId}, 





		AND id = #{id}


		AND create_time = #{createTime}


		AND update_time = #{updateTime}


		AND client_id = #{clientId}


		AND org_code_leve = #{orgCodeLeve}


		AND type = #{type}


		AND type_detail = #{typeDetail}


		AND process_id = #{processId}


		AND creator = #{creator}

