mapper.delete:
DELETE FROM cb_process 


		AND id = #{id}


		AND create_time = #{createTime}


		AND update_time = #{updateTime}


		AND client_id = #{clientId}


		AND process_id = #{processId}


		AND parent_process_id = #{parentProcessId}


		AND name = #{name}


		AND display_name = #{displayName}


		AND type = #{type}


		AND type_detail = #{typeDetail}


		AND status = #{status}


		AND version = #{version}


		AND creator = #{creator}

mapper.update:
UPDATE cb_process 


		id = #{id}, 


		create_time = #{createTime}, 


		update_time = #{updateTime}, 


		client_id = #{clientId}, 


		process_id = #{processId}, 


		parent_process_id = #{parentProcessId}, 


		name = #{name}, 


		display_name = #{displayName}, 


		type = #{type}, 


		type_detail = #{typeDetail}, 


		status = #{status}, 


		version = #{version}, 





		AND id = #{id}


		AND create_time = #{createTime}


		AND update_time = #{updateTime}


		AND client_id = #{clientId}


		AND process_id = #{processId}


		AND parent_process_id = #{parentProcessId}


		AND name = #{name}


		AND display_name = #{displayName}


		AND type = #{type}


		AND type_detail = #{typeDetail}


		AND status = #{status}


		AND version = #{version}


		AND creator = #{creator}

