mapper.delete:
DELETE FROM cb_process_node 


		AND id = #{id}


		AND create_time = #{createTime}


		AND update_time = #{updateTime}


		AND client_id = #{clientId}


		AND process_id = #{processId}


		AND node_id = #{nodeId}


		AND name = #{name}


		AND pre_node_id = #{preNodeId}


		AND next_node_id = #{nextNodeId}


		AND type = #{type}


		AND node_logic = #{nodeLogic}


		AND runner_flag = #{runnerFlag}


		AND next_runner_org = #{nextRunnerOrg}


		AND status = #{status}


		AND runner_user_id = #{runnerUserId}


		AND runner_post_id = #{runnerPostId}


		AND runner_depart_id = #{runnerDepartId}


		AND runner_usergroup_id = #{runnerUsergroupId}


		AND runner_org_level = #{runnerOrgLevel}


		AND is_multiselect = #{isMultiselect}


		AND is_other_man = #{isOtherMan}


		AND is_assign_man = #{isAssignMan}


		AND is_revoke = #{isRevoke}


		AND is_fallback = #{isFallback}

