FROM cb_merchant cm  
LEFT JOIN cb_merchant_user u ON cm.merchant_id = u.merchant_id and u.client_id=#{merchant.clientId}  AND u.merchant_role_id = 100000000 AND u.is_delete = FALSE	 
LEFT JOIN cb_org o ON cm.org_code = o.org_code AND o.client_id= #{merchant.clientId}   
LEFT JOIN cb_merchant_account t ON cm.merchant_id = t.merchant_id and t.client_id = #{merchant.clientId}  and t.outlet_id = '0' and t.is_delete= FALSE and t.acct_type = '1' 



