SELECT u.id, u.create_time, u.client_id, u.merchant_id, u.outlet_id , u.merchant_role_id, u.username, u.password, u.email, u.phone , u.is_rest, u.is_delete, u.realname , m.merchant_name , m.is_enable merchant_is_enable , m.disable_status merchant_disable_status , m.org_code , o.outlet_name FROM cb_merchant_user u LEFT JOIN cb_merchant m ON u.merchant_id = m.merchant_id LEFT JOIN cb_outlet o ON o.outlet_id = u.outlet_id WHERE u.merchant_role_id = #{roleId} AND u.merchant_id in
