mapper.select:
		SELECT 
            id,client_id,create_time,activities_name,points,begin_time,end_time,status,count 
        FROM cb_activities 
        WHERE 1=1

				 AND activities_type = #{activitiesType}


				 AND client_id = #{clientId}


				 AND points = #{points}

		SELECT 
            id,client_id,create_time,activities_name,points,begin_time,end_time,status,count 
        FROM cb_activities 
		WHERE status != 3

				 AND id = #{id}


				 AND client_id = #{clientId}


				 AND create_time = #{createTime}


				 AND activities_name like CONCAT('%',#{activitiesName},'%' )


				 AND points = #{points}


				 AND begin_time = #{beginTime}


				 AND end_time = #{endTime}


				 AND status = #{status}


				 AND count = #{count}


		 ORDER BY create_time DESC

		SELECT 
            id,client_id,create_time,activities_name,points,begin_time,end_time,status,count 
        from cb_activities 
        WHERE status != 3 


					 AND id = #{activities.id}


					 AND client_id = #{activities.clientId}


					 AND create_time = #{activities.createTime}


					 AND activities_name like CONCAT('%',#{activities.activitiesName},'%' )


					 AND points = #{activities.points}


					 AND begin_time = #{activities.beginTime}


					 AND end_time = #{activities.endTime}


					 AND status = #{activities.status}


					 AND count = #{activities.count}


		 ORDER BY create_time DESC
