mapper.insert:
insert into cb_merchant_outlet_favorite
(create_time, update_time, client_id, merchant_id, merchant_outlet_id, merchant_outlet_name, member_code, member_name, member_mobile, is_delete)
values
(
  #{createTime}, #{updateTime}, #{clientId}, #{merchantId}, #{merchantOutletId}, #{merchantOutletName}, #{memberCode}, #{memberName}, #{memberMobile}, #{isDelete}
)

mapper.select:
select * from cb_merchant_outlet_favorite



			and client_id = #{condition.clientId}


			and merchant_Id = #{condition.merchantId}


			and merchant_outlet_id = #{condition.merchantOutletId}


			and merchant_outlet_name like  CONCAT('%',#{condition.merchantOutletName},'%' )


			and member_code =  #{condition.memberCode}


			and member_name like  CONCAT('%',#{condition.memberName},'%' )


			and member_mobile like  CONCAT('%',#{condition.memberMobile},'%' )


			and is_delete = #{condition.isDelete}



order by create_time desc
