mapper.select:
		SELECT 
            u.id, u.create_time, u.client_id, u.merchant_id, u.outlet_id
            , u.merchant_role_id, u.username, u.password, u.email, u.phone
            , u.is_rest, u.is_delete, u.realname
            ,(select m.merchant_name from cb_merchant m where m.merchant_id = u.merchant_id) merchant_name
            ,(select o.outlet_name from cb_outlet o where o.outlet_id = u.outlet_id) outlet_name
            ,(select m.org_code from cb_merchant m where m.merchant_id = u.merchant_id) org_code
        FROM cb_merchant_user u


				 AND u.client_id = #{clientId}


				 AND u.merchant_id = #{merchantId}


				 AND u.outlet_id = #{outletId}


				 AND u.merchant_role_id = #{merchantRoleId}


				 AND u.username like CONCAT('%',#{username},'%' )


				 AND password = #{password}


				 AND u.email like CONCAT('%',#{email},'%' )


				 AND u.phone like CONCAT('%',#{phone},'%' )


				 AND u.is_rest = #{isRest}


				 AND u.is_delete = #{isDelete}


				 AND u.realname like CONCAT('%',#{realname},'%' )


				AND u.id = #{id}

		select 
            u.id, u.create_time, u.client_id, u.merchant_id, u.outlet_id
            , u.merchant_role_id, u.username, u.password, u.email, u.phone
            , u.is_rest, u.is_delete, u.realname
            , m.merchant_name, o.outlet_name, m.org_code
        from cb_merchant_user u, cb_merchant m, cb_outlet o
        where m.merchant_id = u.merchant_id and o.outlet_id = u.outlet_id


				AND u.client_id = #{merchantUser.clientId}


				AND u.merchant_id = #{merchantUser.merchantId}


				AND u.outlet_id = #{merchantUser.outletId}


				AND u.merchant_role_id = #{merchantUser.merchantRoleId}


				AND u.username like CONCAT('%',#{merchantUser.username},'%' )


				AND u.password = #{merchantUser.password}


				AND u.email like CONCAT('%',#{merchantUser.email},'%' )


				AND u.phone like CONCAT('%',#{merchantUser.phone},'%' )


				AND u.is_rest = #{merchantUser.isRest}


				AND u.is_delete = #{merchantUser.isDelete}


				AND u.realname = #{merchantUser.realname}


				AND u.id = #{merchantUser.id}

SELECT u.id, u.create_time, u.client_id, u.merchant_id, u.outlet_id , u.merchant_role_id, u.username, u.password, u.email, u.phone , u.is_rest, u.is_delete, u.realname ,(select m.merchant_name from cb_merchant m where m.merchant_id = u.merchant_id) merchant_name ,(select o.outlet_name from cb_outlet o where o.outlet_id = u.outlet_id) outlet_name ,(select m.org_code from cb_merchant m where m.merchant_id = u.merchant_id) org_code FROM cb_merchant_user u WHERE u.merchant_role_id = #{roleId} AND u.merchant_id in
#{merId}
AND u.is_delete = 0
