                        and data_state = #{pageFilter.filterEntity.dataState,typeHandler=com.froad.fft.persistent.extend.EnumTypeHandler}


                        and client_id = #{pageFilter.filterEntity.clientId}







					 and ${pageFilter.property} >= #{pageFilter.startTime}




